    "id" bigint GENERATED BY DEFAULT AS IDENTITY NOT NULL,
    "region" text NOT NULL,
    "country" text NOT NULL,
    "created_at" timestamp with time zone NOT NULL DEFAULT now(),
    "report_id" text NOT NULL,
    "storage_path" text NOT NULL,
    "settlement_id" text NOT NULL,
    "currency" text NOT NULL,
    "marketplace_name" text NOT NULL,
    "settlement_start_date" timestamp with time zone NOT NULL,
    "settlement_end_date" timestamp with time zone NOT NULL,
    "deposit_date" timestamp with time zone NOT NULL,
    "net_proceeds" numeric(10, 2) NOT NULL,
    "beginning_balance" numeric(10, 2) NOT NULL,
    "account_reserve_level" numeric(10, 2) NOT NULL,
    "sales_total" numeric(10, 2) NOT NULL,
    "sales_product_charges" numeric(10, 2) NOT NULL,
    "sales_tax" numeric(10, 2) NOT NULL,
    "sales_shipping" numeric(10, 2) NOT NULL,
    "sales_inventory_reimbursements" numeric(10, 2) NOT NULL,
    "sales_other" numeric(10, 2) NOT NULL,
    "refunds_total" numeric(10, 2) NOT NULL,
    "refunds_refunded_sales" numeric(10, 2) NOT NULL,
    "refunds_refunded_expenses" numeric(10, 2) NOT NULL,
    "expenses_total" numeric(10, 2) NOT NULL,
    "expenses_fba_fees" numeric(10, 2) NOT NULL,
    "expenses_amazon_fees" numeric(10, 2) NOT NULL,
    "expenses_promo_rebates" numeric(10, 2) NOT NULL,
    "expenses_cost_of_advertising" numeric(10, 2) NOT NULL
);

